import React, {Component} from 'react';
import {Form, Formik} from 'formik';
import * as Yup from 'yup';
import {RadioBox, Select, TextInput} from "./inputTypesForm";
import {Button} from './Button/Button';

import Prestation from "../services/prestation.service";



export  default class  AddServiceForm extends  Component{
    constructor(props) {
        super(props);
        this.state={
            message: "",
            successful: false
        };
    }

    render() {
        return (
            <Formik
                initialValues={
                    {
                        type: '',
                        nom: '',
                        adresse: '',
                        code_postal: '',
                        ville: '',
                        description: '',
                        intervention:'',
                        surface: '',
                        capacity: '',
                        foodType: '',
                        morning: '',
                        evening: '',
                        full_day: '',
                        night: ''
                    }
                }
                validationSchema={ Yup.object({
                    nom: Yup.string()
                        .required('Obligatoire'),
                    adresse: Yup.string()
                        .required('Obligatoire'),
                    code_postal: Yup.number()
                        .required('Obligatoire'),
                    ville: Yup.string()
                        .required('Obligatoire'),
                    descripton: Yup.string()
                        .required('Obligatoire'),
                    intervention: Yup.number()
                        .required('Obligatoire'),
                    morning: Yup.number()
                        .required('Obligatoire'),
                    evening: Yup.string()
                        .required('Obligatoire'),
                    full_day: Yup.string()
                        .required('Obligatoire'),
                    night: Yup.string()
                        .required('Obligatoire'),
                })}
                onSubmit ={ values =>{
                    Prestation.addService(
                        values.type, values.nom, values.adresse, values.code_postal, values.ville,
                        values.description,values.intervention, values.surface, values.capacity, values.foodType,
                        values.morning,values.evening,values.full_day,values.night
                    ).then(
                        response => {
                            this.setState({
                                message: response.data.message,
                                successful: true
                            });
                        },
                        error =>{
                            const resMessage=(error.response && error.response.data && error.response.data.message)
                                || error.message || error.toString();

                            this.setState({
                                message: resMessage,
                                successful: false
                            });
                        }
                    );
                }}
            >
                <Form >
                    {!this.state.successful && (
                        <div>
                            <select
                                className="typePrestation"
                                label="Type de prestation: "
                                name="type"
                            >
                                <option value="" label="-Obligatoire-" />
                                <option value="un endroit" label="un endroit" />
                                <option value="Nourriture" label="Nourriture" />
                                <option value="Agents de sécurité" label="Agents de sécurité" />
                                <option value="une animation" label="une animation" />
                                <option value="célébrités" label="célébrités" />
                                <option value="Animation musicales" label="Animation musicales" />
                                <option value="Mobilier et décoration" label="Mobilier et décoration" />
                                <option value="Photographes" label="Photographes" />
                                <option value="Serveurs" label="Serveurs" />
                                <option value="Transport" label="Transport" />
                            </select>

                            <TextInput
                                label="Nom de la prestation:"
                                name="nom"
                                type="text"
                            />

                            <TextInput
                                label="Nom:"
                                name="lastName"
                                type="text"
                            />

                            <TextInput
                                label="Adresse de votre lieu de travail:"
                                name="adresse"
                                type="text"
                            />

                            <TextInput
                                label="Code postal:"
                                name="code_postal"
                                type="number"
                            />


                            <TextInput
                                label="Ville:"
                                name="ville"
                                type="text"
                            />


                            <TextInput
                                label="Description:"
                                name="description"
                                type="text "
                            />

                            <TextInput
                                label="Rayon d'intervention:"
                                name="intervention"
                                type="number "
                            />

                            {console.log(document.getElementsByClassName("typePrestation")[0].value)}

                            <Button  type="submit">Submit</Button>
                        </div>
                    )}
                    {this.state.message && (
                        <div className="form-group">
                            <div
                                className={
                                    this.state.successful
                                        ? "alert alert-success"
                                        : "alert alert-danger"
                                }
                                role="alert"
                            >
                                {this.state.message}
                            </div>
                        </div>
                    )}
                </Form>
            </Formik>

        );
    }
}
