import React, {Component} from 'react';
import {Form, Formik} from 'formik';
import * as Yup from 'yup';
import {RadioBox, Select, TextInput} from "../inputTypesForm";
import {Button} from '../Button/Button';

import Prestation from "../../services/prestation.service";



export  default class AddPlaceForm extends  Component{
    constructor(props) {
        super(props);
        this.state={
            message: "",
            successful: false
        };
    }

    render() {
        return (
            <Formik
                initialValues={
                    {
                        nom: '',
                        adresse: '',
                        code_postal: '',
                        ville: '',
                        description: '',
                        surface: 0,
                        capacity: 0,
                        morning: 0,
                        evening: 0,
                        full_day: 0,
                        night: 0
                    }
                }
                validationSchema={ Yup.object({
                    nom: Yup.string()
                        .required('Obligatoire'),
                    adresse: Yup.string()
                        .required('Obligatoire'),
                    code_postal: Yup.number()
                        .required('Obligatoire'),
                    ville: Yup.string()
                        .required('Obligatoire'),
                    surface: Yup.number()
                        .required('Obligatoire'),
                    capacity: Yup.number()
                        .required('Obligatoire'),
                    morning: Yup.number()
                        .required('Obligatoire'),
                    evening: Yup.number()
                        .required('Obligatoire'),
                    full_day: Yup.number()
                        .required('Obligatoire'),
                    night: Yup.number()
                        .required('Obligatoire')
                })}
                 onSubmit={ (values) =>{
                   Prestation.addService(
                        "un endroit", values.nom, values.adresse, values.code_postal, values.ville,
                        values.description,null, values.surface, values.capacity, null,
                        values.morning,values.evening,values.full_day,values.night
                    ).then(
                        response => {
                            this.setState({
                                message:  response.data.message,
                                successful: true
                            });
                        },
                        error =>{
                            const resMessage=(error.response && error.response.data && error.response.data.message)
                                || error.message || error.toString();

                            this.setState({
                                message: resMessage,
                                successful: false
                            });
                        }
                    );
                }}
            >
                <Form >
                    {!this.state.successful && (
                        <div>


                            <TextInput
                                label="Nom de la prestation:"
                                name="nom"
                                type="text"
                            />

                            <TextInput
                                label="Adresse de votre lieu de travail:"
                                name="adresse"
                                type="text"
                            />

                            <TextInput
                                label="Code postal:"
                                name="code_postal"
                                type="number"
                            />


                            <TextInput
                                label="Ville:"
                                name="ville"
                                type="text"
                            />


                            <TextInput
                                label="Description:"
                                name="description"
                                type="text "
                            />

                            <TextInput
                                label="Superficie du lieu:"
                                name="surface"
                                type="number "
                            />

                            <TextInput
                                label="Accommodation capacity:"
                                name="capacity"
                                type="number "
                            />




                            <TextInput
                                label="Prix/demi-journée:"
                                name="morning"
                                type="number "
                            />

                            <TextInput
                                label="Prix/journée:"
                                name="evening"
                                type="number "
                            />

                            <TextInput
                                label="Prix/soirée:"
                                name="night"
                                type="number "
                            />

                            <TextInput
                                label="Prix/journée+soirée:"
                                name="full_day"
                                type="number "
                            />


                        </div>
                    )}
                    {this.state.message && (
                        <div className="form-group">
                            <div
                                className={
                                    this.state.successful
                                        ? "alert alert-success"
                                        : "alert alert-danger"
                                }
                                role="alert"
                            >
                                {this.state.message}
                            </div>
                        </div>
                    )}
                    <button type="submit" >Submit</button>
                </Form>
            </Formik>

        );
    }
}
