import React, { useState, useEffect } from 'react';
import { Button } from '../Button/Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import Dropdown from '../Dropdown/Dropdown';

import styled from 'styled-components';
import { Connexion } from '../Connexion PopUp/Connexion';
import { GlobalStyle } from './globalStyles';

/*PopUp l connexion*/
const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;


function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [navbar, setNavbar] = useState(false);
  const [dropdown, setDropdown] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };
  const onMouseEnter = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
  };

  const onMouseLeave = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(false);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

/* L navbar tbadel l couleur mte3ha*/
const changeBackground = () => {
    if (window.scrollY >= 80){
        setNavbar(true);
    }else{
        setNavbar(false);
    }
};

window.addEventListener('scroll', changeBackground);

/*PopUp l connexion*/
const [showConnexion, setShowConnexion] = useState(false);

const openConnexion = () => {
  setShowConnexion(prev => !prev);
};

  return (
    <>
      <nav className={navbar ? 'navbar active' : 'navbar'}>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            <img src='/images/logo.png' alt='' className='logo' />
            ZN EVENT
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>

            <li className='nav-item'>
              <Link to="/" className='nav-links' onClick={closeMobileMenu}>
                Home
              </Link>
            </li>

            <li
            className='nav-item'
            onMouseEnter={onMouseEnter}
            onMouseLeave={onMouseLeave}
            >
              <Link
                to='/Cards'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                 Nos Prestataires <i className='fas fa-caret-down' />
              </Link>
              {dropdown && <Dropdown />}
            </li>
            <li className='nav-item'>
              <Link
                to='/QuiSommesNous'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Qui Sommes Nous?
              </Link>
            </li>

            <li>
              <Link to='/login' className='nav-links-mobile' onClick={closeMobileMenu} >
                Se Connecter
              </Link>
            </li>
          </ul>
          <Container>
             {button && <Button className='btns' buttonStyle='btn--outline' buttonSize='btn--large' onClick={openConnexion} >Se Connecter</Button>}
              <Connexion showConnexion={showConnexion} setShowConnexion={setShowConnexion} />
          </Container>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
