import React, { useRef, useEffect, useCallback } from 'react';
import './Connexion.css';


export const Connexion = ({ showConnexion, setShowConnexion }) => {
  const ConnexionRef = useRef();



  const closeConnexion = e => {
    if (ConnexionRef.current === e.target) {
      setShowConnexion(false);
    }
  };

  const keyPress = useCallback(
    e => {
      if (e.key === 'Escape' && showConnexion) {
        setShowConnexion(false);
        console.log('I pressed');
      }
    },
    [setShowConnexion, showConnexion]
  );

  useEffect(
    () => {
      document.addEventListener('keydown', keyPress);
      return () => document.removeEventListener('keydown', keyPress);
    },
    [keyPress]
  );

  return (
    <>

        <div className='Backgroung' onClick={closeConnexion} ref={ConnexionRef}>
            <div className='ConnexionWrapper' showConnexion={showConnexion}>
              <img src='/images/1.PNG' alt='' className='ConnexionImg' />
              <div className='ConnexionContent'>
                <h1>Are you ready?</h1>
                <p>Get exclusive access to our next launch.</p>
                <button>Join Now</button>
              </div>
              <div className='CloseConnexionButton'
                aria-label='Close Connexion'
                onClick={() => setShowConnexion(prev => !prev)}
              />
            </div>
        </div>

    </>
  );
};
