import React, { useRef, useEffect, useCallback } from 'react';
import styled from 'styled-components';
import { MdClose } from 'react-icons/md';

const Background = styled.div`
  width: 100%;
  height: 100%;
  background: white;
  position: fixed;
  top: 25%;
  left:20%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ConnexionWrapper = styled.div`
  width: 800px;
  height: 500px;
  box-shadow: 0 5px 16px rgba(0, 0, 0, 0.2);
  background: #fff;
  color: #000;
  display: grid;
  grid-template-columns: 1fr 1fr;
  position: relative;
  z-index: 10;
  border-radius: 10px;
`;

const ConnexionImg = styled.img`
  width: 100%;
  height: 100%;
  border-radius: 10px 0 0 10px;
  background: #000;
`;

const ConnexionContent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  line-height: 1.8;
  color: #141414;

  p {
    margin-bottom: 1rem;
  }

  button {
    padding: 10px 24px;
    background: #141414;
    color: #fff;
    border: none;
  }
`;

const CloseConnexionButton = styled(MdClose)`
  cursor: pointer;
  position: absolute;
  top: 20px;
  right: 20px;
  width: 32px;
  height: 32px;
  padding: 0;
  z-index: 10;
`;

export const Connexion = ({ showConnexion, setShowConnexion }) => {
  const ConnexionRef = useRef();

  const closeConnexion = e => {
    if (ConnexionRef.current === e.target) {
      setShowConnexion(false);
    }
  };

  const keyPress = useCallback(
    e => {
      if (e.key === 'Escape' && showConnexion) {
        setShowConnexion(false);
        console.log('I pressed');
      }
    },
    [setShowConnexion, showConnexion]
  );

  useEffect(
    () => {
      document.addEventListener('keydown', keyPress);
      return () => document.removeEventListener('keydown', keyPress);
    },
    [keyPress]
  );

  return (
    <>
      {showConnexion ? (
        <Background onClick={closeConnexion} ref={ConnexionRef}>
            <ConnexionWrapper showConnexion={showConnexion}>
              <ConnexionImg src={require('./accueil.jpg')} alt='camera' />
              <ConnexionContent>
                <h1>Are you ready?</h1>
                <p>Get exclusive access to our next launch.</p>
                <button>Join Now</button>
              </ConnexionContent>
              <CloseConnexionButton
                aria-label='Close Connexion'
                onClick={() => setShowConnexion(prev => !prev)}
              />
            </ConnexionWrapper>
        </Background>
      ) : null}
    </>
  );
};
