import React, { useState, useEffect } from 'react';
import { Button } from '../Button/Button';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [navbar, setNavbar] = useState(false);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

const changeBackground = () => {
    if (window.scrollY >= 80){
        setNavbar(true);
    }else{
        setNavbar(false);
    }
};

window.addEventListener('scroll', changeBackground);

  return (
    <>
      <nav className={navbar ? 'navbar active' : 'navbar'}>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            <img src='/images/logo.png' alt='' className='logo' />
            ZN EVENT
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to="/" className='nav-links' onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className='nav-item'>
              <Link to='/prestataires' className='nav-links' onClick={closeMobileMenu}>
                Nos Prestataires
              </Link>
              <ul>
                <li>
                    <Link to='/Lieux' className='nav-links'>
                       Lieux
                    </Link>
                </li>
                <li>
                    <Link to='/Traiteurs' className='nav-links'>
                       Traiteurs
                    </Link>
                </li>
                <li>
                    <Link to='/Animation' className='nav-links'>
                       Animation
                    </Link>
                </li>
                <li>
                    <Link to='/Communication' className='nav-links'>
                       Communication
                    </Link>
                </li>
                <li>
                    <Link to='/Prestataires' className='nav-links'>
                       Prestataires
                    </Link>
                </li>
                <li>
                    <Link to='/Packages' className='nav-links'>
                       Packages
                    </Link>
                </li>
                
              </ul>
            </li>
            <li className='nav-item'>
              <Link
                to='/QuiSommesNous'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Comment ça marche?
              </Link>
            </li>

            <li>
              <Link to='/login' className='nav-links-mobile' onClick={closeMobileMenu} >
                Se Connecter
              </Link>
            </li>
          </ul>
          {button && <Button className='btns' buttonStyle='btn--outline' buttonSize='btn--large'>Se Connecter</Button>}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
